import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../Login/LoginForm.css';

const Login = () => {
  const navigate = useNavigate();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [result, setResult] = useState('');

  const handleSubmit = async (event) => {
    event.preventDefault();

    const userData = {
      Email: email,
      Password: password,
    };

    try {
      const response = await fetch('https://localhost:44324/api/Users/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(userData),
      });

      if (response.ok) {
        const data = await response.json();

        // Extract role information from the response
        const { token, role } = data;

        // Store the role in localStorage or a state management solution (e.g., Redux)
        // Here, I'm storing it in localStorage as an example
        localStorage.setItem('userRole', role);

        setResult(`Login successful. Token: ${token}`);

        // Redirect based on user role
        if (role === 'admin') {
          navigate('/principale');
        } else {
          navigate('/home');
        }
      } else {
        const errorBody = await response.text();
        throw new Error(errorBody || 'Une erreur s\'est produite avec la requête.');
      }
    } catch (error) {
      setResult(error.toString());
    }
  };

  const handleRegisterClick = () => {
    navigate('/register');
  };

  return (
    <section
    className="container"
    style={{
      backgroundImage: `url(${process.env.PUBLIC_URL}/path-to-your-image/mehdi.png)`,
      backgroundSize: 'cover',
      backgroundPosition: 'center',
    }}
  >
      <div className="login-container">
        <div className="circle circle-one"></div>
        <div className="form-container">
          <h1 className="opacity">LOGIN</h1>
          <form onSubmit={handleSubmit}>
            <input
              type="email"
              placeholder="EMAIL"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
            <input
              type="password"
              placeholder="PASSWORD"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
            <button className="opacity" type="submit">
              SUBMIT
            </button>
          </form>
          <div className="register-forget opacity">
            <a href="#" onClick={handleRegisterClick}>
              REGISTER
            </a>
          </div>
        </div>
        <div className="circle circle-two"></div>
      </div>
      <div className="theme-btn-container"></div>
    </section>
  );
};

export default Login;
