// Principale.js
import React, { useState } from 'react';
import Button from '@mui/material/Button';
import { Link } from 'react-router-dom';
import PatientList from '../Patient/Patientlist';
import MedcinList from '../Medcin/MedcinList';
import RdvList from '../RDV/Listrdv';
import DossierMedicauxList from '../DossierM/Listdossier'; // Import the new component

const Principale = () => {
  const [sectionSelectionnee, setSectionSelectionnee] = useState(null);

  const handleChangementSection = (section) => {
    setSectionSelectionnee(section);
  };

  return (
    <div>
      {/* Barre de navigation */}
      <div style={{ backgroundColor: '#2196f3', padding: '10px', textAlign: 'center' }}>
        <Button onClick={() => handleChangementSection("patients")} style={{ marginRight: '10px', color: 'white' }}>Patients</Button>
        <Button onClick={() => handleChangementSection("doctors")} style={{ marginRight: '10px', color: 'white' }}>Médecins</Button>
        <Button onClick={() => handleChangementSection("rendezvous")} style={{ marginRight: '10px', color: 'white' }}>Rendez-vous</Button>
        <Button onClick={() => handleChangementSection("dossiers")} style={{ color: 'white' }}>Dossiers Médicaux</Button>
      </div>

      {/* Affichage de la section en fonction de la sélection */}
      {(sectionSelectionnee === "patients" && (
        <div>
          <PatientList />
          <Link to="/principale/addpatient">Ajouter un patient</Link>
          
        </div>
      ))}

      {(sectionSelectionnee === "doctors" && (
        <div>
          <MedcinList />
          <Link to="/principale/addmedcins">Ajouter un médecin</Link>
        </div>
      ))}

      {(sectionSelectionnee === "rendezvous" && (
        <div>
          <RdvList />
          {/* Ajoutez ici le lien pour ajouter un rendez-vous */}
          <Link to="/principale/addrdv">Ajouter un rendez-vous</Link>
        </div>
      ))}

      {/* Ajout de la section pour les dossiers médicaux */}
      {(sectionSelectionnee === "dossiers" && (
        <div>
          <DossierMedicauxList />
          {/* Ajoutez ici le lien pour ajouter un dossier médical */}
          <Link to="/principale/adddossier">Ajouter un dossier médical</Link>
        </div>
      ))}

      {/* Ajouter une condition pour afficher le contenu de la section uniquement si une section est sélectionnée */}
      {(!sectionSelectionnee || (sectionSelectionnee !== "patients" && sectionSelectionnee !== "doctors" && sectionSelectionnee !== "rendezvous" && sectionSelectionnee !== "dossiers")) && (
        <div>
          <h2>Bonjour admin</h2>
        </div>
      )}
    </div>
  );
};

export default Principale;
